SELECT 100.00 * table_name.column_name IN ( SELECT table_name.column_name FROM table_name AS table_name GROUP BY table_name.column_name HAVING SUM ( table_name.column_name ) > 300 ) / SUM ( table_name.column_name * ( 1 - table_name.column_name ) ) AS column_label FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE table_name.column_name >= DATE '1995-09-01' AND table_name.column_name < DATE '1995-10-01' 