SELECT column_name , column_name , SUM ( column_name ) AS column_label , SUM ( column_name ) AS column_label , SUM ( column_name * ( 1 - column_name ) ) AS column_label , SUM ( column_name * ( 1 - column_name ) * ( 1 + column_name ) ) AS column_label , AVG ( column_name ) AS column_label , AVG ( column_name ) AS column_label , AVG ( column_name ) AS column_label , COUNT ( * ) AS column_label FROM table_name WHERE table_name.column_name BETWEEN DATE '1995-01-01' AND DATE '1996-12-31' GROUP BY column_name , column_name ORDER BY column_name , column_name 