SELECT column_name , SUM ( CASE WHEN column_name = 'BRAZIL' THEN column_name ELSE 0 END ) / SUM ( column_name ) AS column_label FROM ( SELECT table_name.column_name , COUNT ( table_name.column_name ) AS column_label FROM table_name AS table_name LEFT JOIN column_name AS column_label ON table_name.column_name = table_name.column_name AND table_name.column_name NOT LIKE '%special%requests%' GROUP BY table_name.column_name ) AS table_name GROUP BY column_name ORDER BY column_name 