SELECT SUM ( table_name.column_name ) / 7.0 AS column_label FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE table_name.column_name = 'Brand#23' AND table_name.column_name = 'MED BOX' AND table_name.column_name < ( SELECT 0.2 * AVG ( table_name.column_name ) FROM table_name AS table_name WHERE table_name.column_name = table_name.column_name BETWEEN DATE '1995-01-01' AND DATE '1996-12-31' AND table_name.column_name = 'ECONOMY ANODIZED STEEL' ) 