SELECT column_name , column_name , column_name , SUM ( column_name ) AS column_label FROM ( SELECT table_name.column_name AS column_label , table_name.column_name AS column_label , EXTRACT ( YEAR FROM table_name.column_name ) AS column_label , table_name.column_name * ( 1 - table_name.column_name ) AS column_label FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE ( table_name.column_name = 'FRANCE' AND table_name.column_name = 'GERMANY' ) OR SUM ( CASE WHEN table_name.column_name <> '1-URGENT' AND table_name.column_name <> '2-HIGH' THEN 1 ELSE 0 END ) AND table_name.column_name BETWEEN DATE '1995-01-01' AND DATE '1996-12-31' ) AS table_name GROUP BY column_name , column_name , column_name ORDER BY column_name , column_name , column_name 