SELECT column_name , column_name , column_name , SUM ( column_name ) AS column_label FROM ( SELECT 1 FROM table_name AS table_name WHERE table_name.column_name = table_name.column_name AND table_name.column_name <> table_name.column_name AND table_name.column_name > table_name.column_name ) AS table_name GROUP BY column_name , column_name , column_name ORDER BY column_name , column_name , column_name 