SELECT column_name , COUNT ( * ) AS column_label FROM ( SELECT MIN ( column_name ) FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE table_name.column_name = table_name.column_name AND table_name.column_name = 'EUROPE' ) AS table_name GROUP BY column_name ORDER BY column_name DESC , column_name DESC 