SELECT table_name.column_name , SUM ( table_name.column_name * table_name.column_name ) AS value FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE table_name.column_name = 'GERMANY' GROUP BY table_name.column_name     ORDER BY value DESC 