SELECT table_name.column_name , table_name.column_name , table_name.column_name , table_name.column_name , table_name.column_name , SUM ( table_name.column_name ) AS column_label FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE table_name.column_name IN ( SELECT table_name.column_name FROM ( SELECT table_name.column_name AS column_label , table_name.column_name AS column_label , EXTRACT ( YEAR FROM table_name.column_name ) AS column_label , table_name.column_name * ( 1 - table_name.column_name ) AS column_label FROM table_name AS table_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name JOIN column_name AS column_label ON table_name.column_name = table_name.column_name WHERE ( table_name.column_name = 'FRANCE' AND table_name.column_name = 'GERMANY' ) OR ( table_name.column_name = 'GERMANY' AND table_name.column_name = 'FRANCE' ) AND table_name.column_name BETWEEN DATE '1995-01-01' AND DATE '1996-12-31' ) AS table_name GROUP BY table_name.column_name HAVING SUM ( table_name.column_name ) > 300 ) GROUP BY table_name.column_name , table_name.column_name , table_name.column_name , table_name.column_name , table_name.column_name 